const moveToNext = function (id, arr) {
  let next = id + arr[id];
  // Outofboundary Condition
  next %= arr.length
  // Negative condition
  if (next < 0) next += arr.length;
  return next;
}
function circularArrayLoop(arr) {
  for (let j = 0; j < arr.length; j++) {
    let slowPtr = j;
    let fastPtr = j;
    let isDirChanged = false;

    do {
      let nextSlowPtr = moveToNext(slowPtr, arr);
      let midFastPtr = moveToNext(fastPtr, arr);
      let nextFastPtr = moveToNext(midFastPtr, arr);
      if (
        (arr[slowPtr] > 0 && arr[nextSlowPtr] < 0)
        || (arr[slowPtr] < 0 && arr[nextSlowPtr] > 0)
        || (arr[fastPtr] > 0 && arr[nextFastPtr] < 0)
        || (arr[fastPtr] < 0 && arr[nextFastPtr] > 0)
        || slowPtr === nextSlowPtr
        || fastPtr === midFastPtr
        || midFastPtr === nextFastPtr
    ) {
        isDirChanged = true
        break;
      }
      slowPtr = nextSlowPtr;
      fastPtr = nextFastPtr;
    } while (slowPtr !== fastPtr);
console.log({ slowPtr, fastPtr, isDirChanged })
    if (slowPtr === fastPtr && !isDirChanged) return true;
  }
  return false;
}

function circularArrayLoop1(arr) {
  let currentDirection, fastPtr, slowPtr;
  currentDirection = null;

  for (let i = 0; i < arr.length; i++) {
    if (Math.abs(arr[i]) == arr.length) {
      continue;
    }
    if (arr[i] >= 0) {
      currentDirection = true;
    } else {
      currentDirection = false;
    }
    slowPtr = i;
    fastPtr = i;

    while (slowPtr != fastPtr || slowPtr != -1 || fastPtr != -1) {
      slowPtr = nextStep(arr, slowPtr, currentDirection);
      if (slowPtr == -1) break;

      fastPtr = nextStep(arr, fastPtr, currentDirection);
      if (fastPtr !== -1) {
        fastPtr = nextStep(arr, fastPtr, currentDirection);
      }
    }
    if (slowPtr == fastPtr && slowPtr != -1) {
      return true;
    }
  }
  return false;
}

function nextStep(arr, currentIndex, currentDirection) {
  let nextDirection = arr[currentIndex] >= 0 ? true : false;
  if (nextDirection !== currentDirection) return -1;

  let nextIndex = (arr[currentIndex] + currentIndex) % arr.length;

  if (nextIndex < 0) nextIndex = nextIndex + arr.length;

  if (nextIndex == currentIndex) return -1;
  return nextIndex;
}

